import React, { useState } from "react";
import axios from "axios";
import Photos from "./Photos";

export default function Dictionary() {
  const [keyword, setKeyword] = useState("");
  const [result, setResult] = useState(null);
  const [photos, setPhotos] = useState(null);

  function handleResponse(response) {
    setResult(response.data);
  }

  function handlePexelsResponse(response) {
    setPhotos(response.data.photos);
  }

  function search(event) {
    event.preventDefault();

    const dictionaryApiKey = process.env.REACT_APP_SHECODES_API_KEY;
    const dictionaryApiUrl = `https://api.shecodes.io/dictionary/v1/define?word=${keyword}&key=${dictionaryApiKey}`;
    axios.get(dictionaryApiUrl).then(handleResponse);

    const pexelsApiKey = process.env.REACT_APP_PEXELS_API_KEY;
    const pexelsUrl = `https://api.pexels.com/v1/search?query=${keyword}&per_page=6`;

    axios
      .get(pexelsUrl, {
        headers: { Authorization: `Bearer ${pexelsApiKey}` },
      })
      .then(handlePexelsResponse);
  }

  function handleKeywordChange(event) {
    setKeyword(event.target.value);
  }

  return (
    <div className="Dictionary">
      <form onSubmit={search} className="mb-4">
        <input
          type="search"
          onChange={handleKeywordChange}
          placeholder="Search for a word"
          className="form-control"
        />
      </form>

      {result && (
        <div className="Result mb-5">
          <h2>{result.word}</h2>
          <p>
            <em>{result.phonetic}</em>
          </p>
          <ul className="list-group">
            {result.meanings.map(function (meaning, index) {
              return (
                <li className="list-group-item" key={index}>
                  <strong>{meaning.partOfSpeech}</strong>: {meaning.definition}
                  {meaning.example && (
                    <div className="text-muted mt-2">
                      <em>Example:</em> {meaning.example}
                    </div>
                  )}
                </li>
              );
            })}
          </ul>
        </div>
      )}

      <Photos photos={photos} />
    </div>
  );
}
