import React, { useState } from "react";
import axios from "axios";

export default function Dictionary() {
  const [keyword, setKeyword] = useState("");
  const [result, setResult] = useState(null);

  function handleResponse(response) {
    setResult(response.data);
  }

  function search(event) {
    event.preventDefault();
    const dictionaryApiKey = process.env.REACT_APP_SHECODES_API_KEY;
    const dictionaryApiUrl = `https://api.shecodes.io/dictionary/v1/define?word=${keyword}&key=${dictionaryApiKey}`;
    axios.get(dictionaryApiUrl).then(handleResponse);
  }

  function handleKeywordChange(event) {
    setKeyword(event.target.value);
  }

  return (
    <div className="Dictionary">
      <form onSubmit={search} className="mb-4">
        <div className="input-group">
          <input
            type="search"
            onChange={handleKeywordChange}
            placeholder="Search for a word"
            className="form-control"
          />
          <button type="submit" className="btn btn-primary">
            Search
          </button>
        </div>
      </form>

      {result && result.meanings && Array.isArray(result.meanings) && (
        <div className="Result mb-5">
          <h2>{result.word}</h2>
          {result.phonetic && (
            <p>
              <em>{result.phonetic}</em>
            </p>
          )}

          {result.meanings.map(function (meaning, index) {
            return (
              <div key={index} className="mb-4">
                <h5>{meaning.partOfSpeech}</h5>
                {meaning.definitions &&
                  Array.isArray(meaning.definitions) &&
                  meaning.definitions.map(function (definition, i) {
                    return (
                      <div key={i} className="mb-2">
                        <strong>Definition:</strong> {definition.definition}
                        {definition.example && (
                          <div className="text-muted mt-1">
                            <em>Example:</em> {definition.example}
                          </div>
                        )}
                        {definition.synonyms &&
                          definition.synonyms.length > 0 && (
                            <div className="mt-1">
                              <strong>Synonyms:</strong>{" "}
                              {definition.synonyms.join(", ")}
                            </div>
                          )}
                      </div>
                    );
                  })}
              </div>
            );
          })}
        </div>
      )}
    </div>
  );
}
