import React, { useState } from "react";
import axios from "axios";
import "./App.css";

export default function Dictionary() {
  const [keyword, setKeyword] = useState("");
  const [result, setResult] = useState(null);
  const [photos, setPhotos] = useState([]);

  function handleDictionaryResponse(response) {
    setResult(response.data);
  }

  function handlePexelsResponse(response) {
    setPhotos(response.data.photos.slice(0, 4));
  }

  function search(event) {
    event.preventDefault();

    const dictionaryApiUrl = `https://api.shecodes.io/dictionary/v1/define?word=${keyword}&key=${process.env.REACT_APP_SHECODES_API_KEY}`;
    axios.get(dictionaryApiUrl).then(handleDictionaryResponse);

    const pexelsApiUrl = `https://api.pexels.com/v1/search?query=${keyword}&per_page=4`;
    axios
      .get(pexelsApiUrl, {
        headers: {
          Authorization: process.env.REACT_APP_PEXELS_API_KEY,
        },
      })
      .then(handlePexelsResponse)
      .catch((error) => {
        console.error("Erro na API do Pexels:", error);
        setPhotos([]);
      });
  }

  function handleKeywordChange(event) {
    setKeyword(event.target.value);
  }

  function renderSection(title, items) {
    const hasItems = items && items.length > 0;
    return (
      <div className="col-md-4 mb-4 text-center">
        <div className="card h-100">
          <div className="card-body">
            <h5 className="card-title">{title}</h5>
            <ul className="list-unstyled mb-0">
              {hasItems ? (
                items.map((item, index) => <li key={index}>{item}.</li>)
              ) : (
                <li>.</li>
              )}
            </ul>
          </div>
        </div>
      </div>
    );
  }

  const phonetic = result?.phonetic;
  const word = result?.word;

  const allDefinitions = result?.meanings
    ?.flatMap((meaning) => {
      const fromArray = meaning.definitions?.map((def) => def.definition) || [];
      const single = meaning.definition ? [meaning.definition] : [];
      return [...fromArray, ...single];
    })
    .filter((def) => def && def.trim() !== "");

  const mainDefinitions = allDefinitions?.slice(0, 2) || [];
  const extraDefinitions =
    allDefinitions?.length > 2 ? allDefinitions.slice(2) : [];

  const adjectives = result?.meanings
    ?.filter((m) => m.partOfSpeech === "adjective" && m.definitions)
    ?.flatMap((m) => m.definitions.map((d) => d.definition))
    .filter(Boolean)
    .slice(0, 3);

  const nouns = result?.meanings
    ?.filter((m) => m.partOfSpeech === "noun" && m.definitions)
    ?.flatMap((m) => m.definitions.map((d) => d.definition))
    .filter(Boolean)
    .slice(0, 2);

  const synonyms = result?.meanings
    ?.flatMap((m) => m.synonyms || [])
    .filter((s, i, arr) => arr.indexOf(s) === i)
    .slice(0, 3);

  return (
    <div className="Dictionary container py-4">
      <h1 className="mb-4 text-center">Dictionary App</h1>
      <form onSubmit={search} className="mb-4">
        <div className="input-group">
          <input
            type="search"
            onChange={handleKeywordChange}
            placeholder="Search for a word"
            className="form-control"
          />
          <button type="submit" className="btn btn-primary">
            Search
          </button>
        </div>
      </form>

      {word && (
        <div className="card mb-4">
          <div className="card-body">
            <h2 className="text-capitalize">{word}</h2>
            {phonetic && (
              <p className="text-muted">
                <strong>Phonetic:</strong> <em>{phonetic}</em>
              </p>
            )}
            {mainDefinitions.map((def, index) => (
              <p className="text-muted" key={index}>
                <strong>Definition {index + 1}:</strong> {def}.
              </p>
            ))}
            {extraDefinitions.length > 0 && (
              <details>
                <summary className="text-secondary">
                  See more definitions
                </summary>
                <ul>
                  {extraDefinitions.map((def, index) => (
                    <li key={index}>{def}.</li>
                  ))}
                </ul>
              </details>
            )}
          </div>
        </div>
      )}

      <div className="row">
        {renderSection("Adjectives", adjectives)}
        {renderSection("Nouns", nouns)}
        {renderSection("Synonyms", synonyms)}
      </div>

      {photos.length > 0 && (
        <div className="card mt-4 border-0">
          <div className="card-body">
            <div className="row">
              {photos.map((photo, index) => (
                <div className="col-6 col-md-3 mb-3" key={index}>
                  <img
                    src={photo.src.medium}
                    alt={keyword}
                    className="img-fluid rounded shadow-sm"
                    style={{
                      height: "200px",
                      objectFit: "cover",
                      width: "100%",
                    }}
                  />
                </div>
              ))}
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
