export default function DefinitionCard({ word, phonetic, definitions = [] }) {
  // Função para garantir ponto final no fim da frase, sem duplicar
  const formatDefinition = (text) => {
    if (!text) return "";
    return text.trim().endsWith(".") ? text.trim() : text.trim() + ".";
  };

  return (
    <div className="card mb-4">
      <div className="card-body">
        <h2 className="text-capitalize">{word}</h2>
        {phonetic && (
          <p className="text-muted">
            <strong>Phonetic:</strong> <em>{phonetic}</em>
          </p>
        )}
        {definitions.slice(0, 2).map((def, index) => (
          <p className="text-muted" key={index}>
            <strong>Definition {index + 1}:</strong> {formatDefinition(def)}
          </p>
        ))}
        {definitions.length > 2 && (
          <details aria-label="See more definitions">
            <summary className="text-secondary">See more definitions</summary>
            <ul>
              {definitions.slice(2).map((def, index) => (
                <li key={index}>{formatDefinition(def)}</li>
              ))}
            </ul>
          </details>
        )}
      </div>
    </div>
  );
}
