import React, { useState } from "react";
import axios from "axios";

export default function Dictionary() {
  const [keyword, setKeyword] = useState("");
  const [result, setResult] = useState(null);

  function handleResponse(response) {
    setResult(response.data);
  }

  function search(event) {
    event.preventDefault();
    const dictionaryApiKey = process.env.REACT_APP_SHECODES_API_KEY;
    const dictionaryApiUrl = `https://api.shecodes.io/dictionary/v1/define?word=${keyword}&key=${dictionaryApiKey}`;
    axios.get(dictionaryApiUrl).then(handleResponse);
  }

  function handleKeywordChange(event) {
    setKeyword(event.target.value);
  }

  return (
    <div className="container py-5">
      <h1 className="text-center mb-4">Dictionary App</h1>

      <form onSubmit={search} className="mb-4">
        <div className="input-group">
          <input
            type="search"
            onChange={handleKeywordChange}
            placeholder="Search for a word"
            className="form-control"
          />
          <button type="submit" className="btn btn-primary">
            Search
          </button>
        </div>
      </form>

      {result && (
        <div className="Result">
          <h2>{result.word}</h2>
          <p>
            <em>{result.phonetic}</em>
          </p>

          {Array.isArray(result.meanings) &&
            result.meanings.map(
              (meaning, index) =>
                Array.isArray(meaning.definitions) &&
                meaning.definitions.map((definition, defIndex) => (
                  <div
                    key={`${index}-${defIndex}`}
                    className="mb-4 border rounded p-3"
                  >
                    <strong>{meaning.partOfSpeech}:</strong>{" "}
                    {definition.definition}
                    {definition.synonyms && definition.synonyms.length > 0 && (
                      <div className="mt-2">
                        <strong>Synonyms:</strong>{" "}
                        {definition.synonyms.join(", ")}
                      </div>
                    )}
                  </div>
                ))
            )}
        </div>
      )}
    </div>
  );
}
